import actions from "../../store/actions";

//Etape 2 : initialiser le State
const initialState = {
  minions: 0,
};
//Etape 3: creer le reducer(ce qui nous permettra de modifier le state selon une action)
const reducer = (state = initialState, action) => {
  // Action
  switch (action.type) {
    case actions.CREATE_MINION:
      return {
        minions: state.minions + 1,
      };
    case actions.DESTROY_MINION:
      return {
        minions: state.minions - 1,
      };
    case actions.CREATE_TEAM:
      return {
        minions: state.minions + action.value,
      };
    case actions.DESTROY_TEAM:
      return {
        minions: state.minions - action.value,
      };
    default:
      return state;
  }
};

export default reducer;
